        var remoteApp = true;
        var ws;
        var userID = -1;
        var useWebSocketsFlag = true;
        var box = '{{{payload.box}}}';
        var heartBeatImage = false;
        var wsUri = '';
        var wsConnected = false;

        function setRemoteApp(remoteAppSet)
        {
            remoteApp  = remoteAppSet;
        }
        function webSocketConnected()
        {
            return wsConnected;
        }
        function useWebSockets(webSocketsFlag)
        {
            useWebSocketsFlag = webSocketsFlag;
        }
        function wsConnectC()
        {
            if (!useWebSocketsFlag)
            {
                return;
            }
            if (wsUri.length < 1)
            {
                var uri = window.location.href.split('://');
                var wslead = 'ws://';
                if (uri[0] == 'https') wslead = 'wss://';
                var questionLocation = uri[1].indexOf('?');
                if (questionLocation >= 0)
                {
                    uri[1] = uri[1].substring(0,questionLocation);
//                    console.log(uri[1]);
                }
                if (uri[1].indexOf('/') < (uri[1].length - 1))
                {
                    wsUri = wslead + uri[1] + '/websocket';
                }
                else
                {
                    wsUri = wslead + uri[1] + 'websocket';
                }
            }
            ws = new WebSocket(wsUri);
            ws.onmessage = function(event)
            {
                onWebSocketMessage(JSON.parse(event.data));

            };
            ws.onopen = function()
            {
                console.log("Websocket connected");
                wsConnected = true;
                $('#webSocketStatus').html('Open');
                $('#reOpenWebSocketButton').hide();
                $('#webSocketStatusRow').css('padding-bottom','0px');
                $('#webSocketStatusCard').hide();
                onWebSocketOpen();
            };
            ws.onclose = function()
            {
                console.log("Websocket closed");
                wsConnected = false;
                $('#webSocketStatus').html('Closed');
                $('#reOpenWebSocketButton').show();
                $('#webSocketStatusCard').show();
                $('#webSocketStatusRow').css('padding-bottom','25px');
                onWebSocketClose();
                wsConnectC();
            };
        }

        function getRandomInt(max)
        {
          return Math.floor(Math.random() * Math.floor(max));
        }
        $( document ).ready(function()
        {
            userID  = {{{payload.userID}}};
            if (!useWebSocketsFlag)
            {
                $('#webSocketStatusRow').css('padding-bottom','0px');
                $('#webSocketStatusCard').hide();
            }
            wsConnectC();
            onDocumentReady();
       });
        $( function() 
        {
            $( "#acknowledgeDialog" ).dialog(
                {
                    width:    500,
                    autoOpen: false,
                    buttons:
                    [
                        {
                            text: "Ok",
                            click: function() { $( this ).dialog( "close" );},
                            showText: false
                        },
                   ]
                });
            $( "#messageDialog" ).dialog(
                {
                    width:    500,
                    autoOpen: false,
                    buttons:[]
                });
        } );
        function optionDialog(headerText, title, text, buttonTexts, buttonFunctions)
        {
            var buttonDefinitions = [];
            for (var ii = 0; ii < buttonTexts.length; ++ii)
            {
                buttonDefinitions[ii] = 
                    {
                        text: buttonTexts[ii],
                        click: buttonFunctions[ii],
                        showText: false
                    };
            }
            $( "#optionDialog" ).dialog( "option", "title", headerText );   
            $( "#optionDialog" ).dialog( "option", "buttons", buttonDefinitions);
            $( "#optionDialogTitle" ).html(title);
            $( "#optionDialogText" ).html(text);
            $( "#optionDialog" ).dialog( "open" );
        }
        $( function() 
        {
            $( "#optionDialog" ).dialog(
                {
                    width:    500,
                    autoOpen: false,
                    buttons:
                    [
                        {
                            text: "Ok",
                            click: function() {$( this ).dialog( "close" );},
                            showText: false
                        },
                        {
                            text: "Cancel",
                            click: function() {$( this ).dialog( "close" );},
                            showText: false
                        },
                   ]
                });
        } );
        function acknowledgeDialog(headerText, title, text)
        {
            $( "#acknowledgeDialog" ).dialog( "option", "title", headerText );    
            $( "#acknowledgeDialogTitle" ).html(title);
            $( "#acknowledgeDialogText" ).html(text);
            $( "#acknowledgeDialog" ).dialog( "open" );
        }
        function messageDialog(headerText, title, text)
        {
            $( "#messageDialog" ).dialog( "option", "title", headerText );    
            $( "#messageDialogTitle" ).html(title);
            $( "#messageDialogText" ).html(text);
            $( "#messageDialog" ).dialog( "open" );
        }
        function heartbeat()
        {
            heartBeatImage = !heartBeatImage;
            var heartBeatImageSrc = '/img/heartRed.png';
            if (heartBeatImage) heartBeatImageSrc = '/img/heartPink.png';
            $('#heartBeatID').attr('src',heartBeatImageSrc);
        }
        function getCookie(extension)
        {
            var cookies = document.cookie.split(';');
            var token = null;
            var cookieName = box + extension + "=";
            for (var icookie = 0; icookie < cookies.length; ++icookie)
            {
                var index = cookies[icookie].indexOf(cookieName);
                if (index >= 0)
                {
                    token = cookies[icookie].substring(index + cookieName.length);
                }
            }
            return token;
        }
        function sendActionMsg(topic,role,actionMsg)
        {
            var roleToken = "";
            if (remoteApp) roleToken  = getCookie('Role');
            if (roleToken == null)
            {
                location.reload();
                return;
            }
            var webSocketMsg = 
            {
                topic     : topic,
                payload   : actionMsg,
                userID    : userID,
                token     : getCookie('Role'),
                role      : role
            };
            ws.send(JSON.stringify(webSocketMsg));
        }

