func:
var errMsg = null;
var tokenError = msg.error;
if (tokenError != undefined)
{
    errMsg = 
    {
        topic : msg.topic, 
        payload:
        {
            topic:'badToken', 
            payload: msg.topic + " badToken",
            userID : msg.userID,
            role   : msg.role
        }
    };
    return [null,errMsg];
}
if (msg.token.expDate > 0)
{
    if (msg.token.expDate < new Date().getTime())
    {
        errMsg = 
        {
            topic : msg.topic, 
            payload:
            {
                topic:'loginExpired', 
                payload: msg.topic + " login expired",
                userID : msg.userID,
                role   : msg.role
            }
        };
        return [null,errMsg];
    }
}
permittedRole = false;
for (itoken = 0; itoken < msg.token.roles.length; ++itoken)
{
    if (msg.token.roles[itoken] == msg.role) permittedRole = true;
}
if (permittedRole) return [msg,null];

errMsg = 
{
    topic : msg.topic, 
    payload:
    {
        topic  : 'permissionError', 
        payload: msg.topic + " not permitted",
        userID : msg.userID,
        role   : msg.role
    }
};

return [null,errMsg];

