        class UserCard 
        {
            constructor(app, config)
            {
                this.app = app;
                this.config = config;
            }
            onDocumentReady()
            {
                let containerRowDiv = document.createElement('div');
                containerRowDiv.setAttribute('class', 'row userCard-containerRow');
                document.getElementById('appContent').appendChild(containerRowDiv);

                let containerColDiv = document.createElement('div');
                containerColDiv.setAttribute('class', 'col-md-12');
                containerColDiv.setAttribute('align', 'center');
                containerRowDiv.appendChild(containerColDiv);

                let cardDiv = document.createElement('div');
                cardDiv.setAttribute('class', 'card userCard');
                containerColDiv.appendChild(cardDiv);

                let titleTable = document.createElement('table');
                titleTable.setAttribute('width', '100%');
                let titleRow  = document.createElement('tr');
                let titleCol  = document.createElement('td');
                titleCol.setAttribute('width', '85%');
                titleCol.setAttribute('align', 'left');
                let titleSpan  = document.createElement('span');
                titleSpan.setAttribute('class', 'userCard-title');
                titleSpan.innerHTML = this.config.title;
                titleCol.appendChild(titleSpan);
                titleRow.appendChild(titleCol);
                let buttonCol  = document.createElement('td');
                buttonCol.setAttribute('width', '15%');

                this.displayButton = document.createElement('button');
                this.displayButton.setAttribute('class', 'btn btn-block userCard-title-button');
                this.displayButton.innerHTML = '-';
                this.displayButton.onclick = event => {this.toggle()};
                buttonCol.appendChild(this.displayButton);
                titleRow.appendChild(buttonCol);

                this.cardBodyDiv = document.createElement('div');
                this.cardBodyDiv.setAttribute('class', 'userCard-body');
                this.cardBodyDiv.setAttribute('align', 'center');
                let cardBodyTable = document.createElement('table');
                cardBodyTable.setAttribute('width', '100%');

                let cardBodyR1 = document.createElement('tr');
                cardBodyTable.appendChild(cardBodyR1);
                let cardBodyR1C1 = document.createElement('td');
                cardBodyR1C1.setAttribute('width', '100%');
                cardBodyR1.appendChild(cardBodyR1C1);

                let usrT1 = document.createElement('table');
                usrT1.setAttribute('width', '100%');
                cardBodyR1C1.appendChild(usrT1);

                let usrT1R1 = document.createElement('tr');
                usrT1.appendChild(usrT1R1);

                let usrT1R1C1 = document.createElement('td');
                usrT1R1C1.setAttribute('width', '50%');
                usrT1R1C1.setAttribute('align', 'left');
                usrT1R1.appendChild(usrT1R1C1);

                let usrT1R1C1Span = document.createElement('span');               
                usrT1R1C1Span.setAttribute('class', 'userCard-label');
                usrT1R1C1Span.innerHTML = 'User:';
                usrT1R1C1.appendChild(usrT1R1C1Span);

                let usrT1R1C2 = document.createElement('td');
                usrT1R1C2.setAttribute('width', '50%');
                usrT1R1C2.setAttribute('align', 'right');
                usrT1R1.appendChild(usrT1R1C2);

                this.userNameSpan = document.createElement('span');               
                this.userNameSpan.setAttribute('class', 'userCard-data');
                this.userNameSpan.innerHTML = '?';
                usrT1R1C2.appendChild(this.userNameSpan);

                let usrT1R2 = document.createElement('tr');
                usrT1.appendChild(usrT1R2);

                let usrT1R2C1 = document.createElement('td');
                usrT1R2C1.setAttribute('width', '50%');
                usrT1R2C1.setAttribute('align', 'left');
                usrT1R2.appendChild(usrT1R2C1);

                let usrT1R2C1Span = document.createElement('span');               
                usrT1R2C1Span.setAttribute('class', 'userCard-label');
                usrT1R2C1Span.innerHTML = 'Time left:';
                usrT1R2C1.appendChild(usrT1R2C1Span);

                let usrT1R2C2 = document.createElement('td');
                usrT1R2C2.setAttribute('width', '50%');
                usrT1R2C2.setAttribute('align', 'right');
                usrT1R2.appendChild(usrT1R2C2);

                this.expTimeSpan = document.createElement('span');               
                this.expTimeSpan.setAttribute('class', 'userCard-data');
                this.expTimeSpan.innerHTML = '?';
                usrT1R2C2.appendChild(this.expTimeSpan);

                let cardBodyR2 = document.createElement('tr');
                cardBodyTable.appendChild(cardBodyR2);
                let cardBodyR2C1 = document.createElement('td');
                cardBodyR2C1.setAttribute('width', '100%');
                cardBodyR2.appendChild(cardBodyR2C1);

                let usrT2 = document.createElement('table');
                usrT2.setAttribute('width', '100%');
                cardBodyR2C1.appendChild(usrT2);

                let usrT2R1 = document.createElement('tr');
                usrT2.appendChild(usrT2R1);

                let usrT2R1C1 = document.createElement('td');
                usrT2R1C1.setAttribute('width', '40%');
                usrT2R1C1.setAttribute('align', 'center');
                usrT2R1.appendChild(usrT2R1C1);

                let renewButton = document.createElement('button');
                renewButton.setAttribute('class', 'btn btn-block userCard-button');
                renewButton.innerHTML = 'Renew';
                renewButton.onclick = event => {this.renew()};
                usrT2R1C1.appendChild(renewButton);

                let usrT2R1C2 = document.createElement('td');
                usrT2R1C2.setAttribute('width', '10%');
                usrT2R1.appendChild(usrT2R1C2);

                let usrT2R1C3 = document.createElement('td');
                usrT2R1C3.setAttribute('width', '40%');
                usrT2R1C3.setAttribute('align', 'center');
                usrT2R1.appendChild(usrT2R1C3);

                let logoutButton = document.createElement('button');
                logoutButton.setAttribute('class', 'btn btn-block userCard-button');
                logoutButton.innerHTML = 'Logout';
                logoutButton.onclick = event => {this.logout()};
                usrT2R1C3.appendChild(logoutButton);

                this.cardBodyDiv.appendChild(cardBodyTable);

                titleTable.appendChild(titleRow);
                cardDiv.appendChild(titleTable);
                cardDiv.appendChild(this.cardBodyDiv);
                if (this.config.displayOnOpen)
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = '-';
                }
                else
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = '+';
                }
                this.getCreds();
                setInterval(() => {this.getCreds()}, 1000);

            }
            toggle()
            {
                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = '-';
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = '+';
                }

            }
            getCreds()
            {
                let now = new Date().getTime();
                let expDate = Number(this.app.getCookie('ExpDate'));
                if (expDate > 0)
                {
                    expDate = expDate - now;
                    if(expDate < 0)
                    {
                        logout();
                    }
                    else
                    {
                        let hours = Math.floor(expDate / 3600000);
                        let minutes = expDate - hours * 3600000;
                        minutes = Math.floor(minutes / 60000);
                        let seconds = expDate - (hours * 3600000) - (minutes * 60000);
                        seconds = Math.floor(seconds / 1000);
                        hours = hours.toString();
                        if (hours.length == 1) hours = '0' + hours; 
                        minutes = minutes.toString();
                        if (minutes.length == 1) minutes = '0' + minutes; 
                        seconds = seconds.toString();
                        if (seconds.length == 1) seconds = '0' + seconds; 
                        let timeString = hours + ':' + minutes + ':' + seconds;
                        this.userNameSpan.innerHTML = this.app.getCookie('Username');
                        this.expTimeSpan.innerHTML = timeString;
                    }
                }
                else
                {
                    this.userNameSpan.innerHTML = this.app.getCookie('Username');
                    this.expTimeSpan.innerHTML = 'Forever';
                }
            }
            logout()
            {
                document.cookie = this.app.box + "Role= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
                document.cookie = this.app.box + "ExpDate= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
                document.cookie = this.app.box + "Username= ; expires = Thu, 01 Jan 1970 00:00:00 GMT";
                window.location.href = "/";
            }
            renew()
            {
                let actionMsg = {};
                this.app.sendActionMsg('renew', 'renew', actionMsg);
            }
            downloadToken()
            {
                downloadString(this.getCookie("Role"), "text/plain", box + "-token.txt");
            }
    // from https://gist.github.com/danallison/3ec9d5314788b337b682
            downloadString(text, fileType, fileName) 
            {
                let blob = new Blob([text], { type: fileType });

                let a = document.createElement('a');
                a.download = fileName;
                a.href = URL.createObjectURL(blob);
                a.dataset.downloadurl = [fileType, a.download, a.href].join(':');
                a.style.display = "none";
                document.body.appendChild(a);
                a.click();
                document.body.removeChild(a);
                setTimeout(function() { URL.revokeObjectURL(a.href); }, 1500);
            }    
        }

