    for (let icss = 0; icss < msg.payload.styleSheets[istyle].css.length; ++icss)
    {
        cssString = cssString + jsonToCss(msg.payload.styleSheets[istyle].css[icss]);
    }
}

msg.payload = {config :config,  urlInput :urlInput, title:msg.payload.title, allowedRoles : msg.payload.allowedRoles, css : cssString, userID:getRandomInt(32768)};
return msg;

// From http://www.featureblend.com/css-json.html
function jsonToCss(cssjson)
{
    var styleStr = "\t\t";
    for(var i in cssjson)
    {
        styleStr += i + "\n\t\t{\n"
        for(var j in cssjson[i]){
            styleStr += "\t\t\t" + j + "\t\t:\t" + cssjson[i][j] + ";\n"     
        }
        styleStr += "\t\t}\n"  
    }
    return styleStr;
}
function getRandomInt(max)
{
  return Math.floor(Math.random() * Math.floor(max));
}

msg['urlInput'] = {};
msg['urlInput']['box'] = global.get('box');
let trayText = msg.payload.trays.split(',');
let trays = [];
for (let it = 0; it < trayText.length; ++it)
{
    let trayText2 = trayText[it].split('.');
    let itray = {type:trayText2[0], name:trayText2[1]};
    trays.push(itray);
}
msg['urlInput']['trayList'] = trays;
msg.payload = [{name : msg.payload.appConfig},{projection  :{_id : 0} }]

return msg;

