                buttonTableTd1.setAttribute('width', '50%');
                buttonTableTd1.setAttribute('align', 'center');
                buttonTableTd1.setAttribute('style', 'padding-top:20px;');
                buttonTableTd2.setAttribute('width', '50%');
                buttonTableTd2.setAttribute('style', 'padding-top:20px;');
