                        this.alarmButton.src="/img/redLight.png";
                        break;
                    default:
                        this.alarmButton.src="/img/greyLight.png";
                        break;

                }
            }
            changeAlarmConfig()
            {
                this.card.app.alarmLimitDialog.editAlarmConfig(this.config.cubeName, this.tray);
            }
            setGaugeWidget(cubeObj)
            {
                let degrees = 180.0 * (cubeObj.alarm.limits.hihi - cubeObj.value) / (cubeObj.alarm.limits.hihi - cubeObj.alarm.limits.lolo);
                if (degrees < 0.0 )   degrees = 0.0;
                if (degrees > 180.0 ) degrees = 180.0;
                // Trig to calc meter point
                let radius = 0.50;
                let radians = degrees * Math.PI / 180;
                let x = radius * Math.cos(radians);
                let y = radius * Math.sin(radians);
                var path1 = (degrees < 45 || degrees > 135) ? 'M -0.0 -0.025 L 0.0 0.025 L ' : 'M -0.025 -0.0 L 0.025 0.0 L ';
                // Path: may have to change to create a better triangle
                let mainPath = path1,
                    pathX = String(x),
                    space = ' ',
                    pathY = String(y),
                    pathEnd = ' Z';
                let rotation = (180.0 * (cubeObj.alarm.limits.high - cubeObj.alarm.limits.lolo) / (cubeObj.alarm.limits.hihi - cubeObj.alarm.limits.lolo)) - 90.0;
                let path = mainPath.concat(pathX,space,pathY,pathEnd);

                let valueColor = this.config.backgroundColor; 

                if ( (cubeObj.alarm.limits.lolo <= cubeObj.value) && (cubeObj.value < cubeObj.alarm.limits.low) )
                {
                    valueColor = this.config.lowColor;
                }
                if ( (cubeObj.alarm.limits.low  <= cubeObj.value) && (cubeObj.value < cubeObj.alarm.limits.high) )
                {
                    valueColor = this.config.mediumColor;
                }
                if ( (cubeObj.alarm.limits.high <= cubeObj.value) && (cubeObj.value < cubeObj.alarm.limits.hihi) )
                {
                  valueColor = this.config.highColor;
                }
                if ( cubeObj.alarm.limits.hihi <= cubeObj.value)
                {
                    valueColor = this.config.highColor;
                }

                let data = 
                [
                    { 
                        type: 'scatter',
                        x: [0], y:[0],
                        marker: {size: 14, color:valueColor},
                        showlegend: false,
                        text: cubeObj.value.toString(),
                        hoverinfo: 'text',


                    },
                    { 
                        values: 
                            [
                                cubeObj.alarm.limits.hihi - cubeObj.alarm.limits.high, 
                                cubeObj.alarm.limits.high - cubeObj.alarm.limits.low, 
                                cubeObj.alarm.limits.low  - cubeObj.alarm.limits.lolo, 
                                cubeObj.alarm.limits.hihi - cubeObj.alarm.limits.lolo 
                            ],
                        rotation: rotation,
                        text: 
                            [
                                cubeObj.alarm.limits.high.toString() + '-' + cubeObj.alarm.limits.hihi.toString(), 
                                cubeObj.alarm.limits.low.toString()  + '-' + cubeObj.alarm.limits.high.toString(), 
                                cubeObj.alarm.limits.lolo.toString() + '-' + cubeObj.alarm.limits.low.toString(), 
                                ''
                            ],
                        textinfo: 'text',
                        textposition:'inside',
                        textfont: {color: this.config.labelColor, size:this.config.textsize},
                        marker: 
                            {
                                colors:
                                    [
                                        this.config.highColor,
                                        this.config.mediumColor,
                                        this.config.lowColor, 
                                        this.config.backgroundColor
                                    ]
                            },
                        hoverinfo: 'label',
                        hole: 0.5,
                        type: 'pie',
                        showlegend: false,
                        sort: false
                    }
                ];
                let layout = 
                {
                    shapes:
                        [
                            {
                                type: 'path',
                                path: path,
                                fillcolor: valueColor,
                                line: {color: valueColor }
                            }
                        ],
                    height: this.config.size,
                    width: this.config.size,
                    title: 
                        {
                            text: cubeObj.value.toString() + ' ' + cubeObj.unit,
                            font: {color: this.config.labelColor, size:this.config.textsize},
                         },
                    xaxis: 
                        {
                            zeroline:false, 
                            showticklabels:false,
                            showgrid: false, 
                            range: [-1, 1],
                        },
                    yaxis: 
                        {
                            zeroline:false, 
                            showticklabels:false,
                            showgrid: false, 
                            range: [-1, 1]
                        },
                    margin: 
                        {
                            t: 80, //top margin
                            l: 0, //left margin
                            r: 0, //right margin
                            b: 20 //bottom margin
                        },
                    plot_bgcolor:this.config.backgroundColor,
                    paper_bgcolor:this.config.backgroundColor

                };

                Plotly.newPlot(this.htmlId, data, layout, {'displayModeBar': false});
            }
        }

//hbarCube
        class HbarCube 
        {
            constructor(card, config)
            {
                this.card = card;
                this.config = JSON.parse(JSON.stringify(config));
                this.htmlId = 'hbarPlot' + Math.floor(Math.random() * 32768).toString();
            }
            onDocumentReady(parentElement)
            {
                let t1 = document.createElement('table');
                parentElement.appendChild(t1);
                t1.setAttribute('width', '100%');
                let r1  = document.createElement('tr');
                t1.appendChild(r1);
                let r1c1  = document.createElement('td');
                r1.appendChild(r1c1);
                r1c1.setAttribute('width', '100%');
                r1c1.setAttribute('align', 'center');
                let labelSpan  = document.createElement('span');
                labelSpan.setAttribute('class', 'hbarCube-label');
                if ('cubeLabel' in this.config)
                {
                    labelSpan.innerHTML = this.config.cubeLabel;
                }
                else
                {
                    labelSpan.innerHTML = this.card.app.trays[this.config.trayIndex].type + "." + this.card.app.trays[this.config.trayIndex].name + "." + this.config.cubeName;
                }
                r1c1.appendChild(labelSpan);
                let r2  = document.createElement('tr');
                t1.appendChild(r2);
                let r2c1  = document.createElement('td');
                r2.appendChild(r2c1);
                let t2 = document.createElement('table');
                r2c1.appendChild(t2);
                t2.setAttribute('width', '100%');
                let r3  = document.createElement('tr');
                t2.appendChild(r3);
                let r3c1  = document.createElement('td');
                r3.appendChild(r3c1);
                r3c1.setAttribute('class', 'hbarCube-column');

                this.gaugeDiv = document.createElement('div');
                this.gaugeDiv.setAttribute('id', this.htmlId);
                this.gaugeDiv.setAttribute('class', 'hbarCube-div');
                this.gaugeDiv.setAttribute('style', 'max-height: calc(' + (this.config.size / 2).toString() + 'px + 20px) !important;');

                r3c1.appendChild(this.gaugeDiv);

                let alarmColumn  = document.createElement('td');
                alarmColumn.setAttribute('class', 'hbarCube-alarmConfig-column');
                this.alarmButton = document.createElement('input');
                this.alarmButton.setAttribute('type', 'image');
                this.alarmButton.setAttribute('src', '/img/greenLight.png');
                this.alarmButton.setAttribute('class', 'hbarCube-alarmConfig-button');
                this.alarmButton.onclick = event => {this.changeAlarmConfig()};
                alarmColumn.appendChild(this.alarmButton);

                r3.appendChild(alarmColumn);
            }

            trayUpdate(trays)
            {
                this.tray = trays[this.config.trayIndex];
                this.setHorzBarWidget(this.tray[this.config.cubeName]);
                switch(this.tray[this.config.cubeName].alarm.value)
                {
                    case 0:
                        this.alarmButton.src="/img/greenLight.png";
                        break;
                    case 1:
                        this.alarmButton.src="/img/blueLight.png";
                        break;
                    case 2:
                        this.alarmButton.src="/img/yellowLight.png";
                        break;
                    case 3:
                        this.alarmButton.src="/img/purpleLight.png";
                        break;
                    case 4:
                        this.alarmButton.src="/img/redLight.png";
                        break;
                    default:
                        this.alarmButton.src="/img/greyLight.png";
                        break;

                }
            }
            changeAlarmConfig()
            {
                this.card.app.alarmLimitDialog.editAlarmConfig(this.config.cubeName, this.tray);
            }
            setHorzBarWidget(cubeObj)
            {
                let lowValue = 0;
                let mediumValue = 0;
                let highValue = 0;

                let lowColor = this.config.backgroundColor;
                let mediumColor = this.config.backgroundColor;
                let highColor = this.config.backgroundColor;
                let valueColor = this.config.backgroundColor; 

                if ( cubeObj.value <= cubeObj.alarm.limits.lolo)
                {
                    lowValue = cubeObj.alarm.limits.lolo;
                    lowColor = this.config.lowColor;
                    valueColor = lowColor;
                }
                if ( (cubeObj.alarm.limits.lolo <= cubeObj.value) && (cubeObj.value < cubeObj.alarm.limits.low) )
                {
                    lowValue = cubeObj.value;
                    lowColor = this.config.lowColor;
                    valueColor = lowColor;
                }
                if ( (cubeObj.alarm.limits.low  <= cubeObj.value) && (cubeObj.value < cubeObj.alarm.limits.high) )
                {
                    lowValue = cubeObj.alarm.limits.low;
                    mediumValue = cubeObj.value - cubeObj.alarm.limits.low; 
                    lowColor = this.config.mediumColor;
                    mediumColor = this.config.mediumColor;
                    valueColor = mediumColor;

                }
                if ( (cubeObj.alarm.limits.high <= cubeObj.value) && (cubeObj.value < cubeObj.alarm.limits.hihi) )
                {
                    lowValue = cubeObj.alarm.limits.low;
                    mediumValue = cubeObj.alarm.limits.high - cubeObj.alarm.limits.low;
                    highValue = cubeObj.value - cubeObj.alarm.limits.high;
                    lowColor = this.config.highColor;
                    mediumColor = this.config.highColor;
                    highColor = this.config.highColor;
                    valueColor = highColor;
                }
                if ( cubeObj.alarm.limits.hihi <= cubeObj.value)
                {
                    lowValue = cubeObj.alarm.limits.low;
                    mediumValue = cubeObj.alarm.limits.high - cubeObj.alarm.limits.low; 
                    highValue = cubeObj.alarm.limits.hihi - cubeObj.alarm.limits.high; 
                    lowColor = this.config.highColor;
                    mediumColor = this.config.highColor;
                    highColor = this.config.highColor;
                    valueColor = highColor;
                }

                let lowBar = 
                {
                    y: ['', '-'],
                    x: [ cubeObj.alarm.limits.low, lowValue],
                    type: 'bar',
                    orientation: 'h',
                    showlegend: false,
                    marker:{color: [this.config.lowColor, lowColor]},
                    width: [0.1, 1.0],
                };
                let mediumBar = 
                {
                    y: ['', '-'],
                    x: [cubeObj.alarm.limits.high - cubeObj.alarm.limits.low, mediumValue],
                    type: 'bar',
                    orientation: 'h',
                    showlegend: false,
                    marker:{color: [this.config.mediumColor, mediumColor]},
                    width: [0.1, 1.0],
                };
                let highBar = 
                {
                    y: ['', '-'],
                    x: [cubeObj.alarm.limits.hihi - cubeObj.alarm.limits.high, highValue],
                    orientation: 'h',
                    type: 'bar',
                    showlegend: false,
                    marker:{color: [this.config.highColor, highColor]},
                    width: [0.1, 1.0],
                };
                let layout = 
                {
                    barmode: 'stack',
                    plot_bgcolor:this.config.backgroundColor,
                    paper_bgcolor:this.config.backgroundColor,
                    margin: 
                    {
                        t: 70, //top margin
                        l: 20, //left margin
                        r: 20, //right margin
                        b: 70 //bottom margin
                    },

                    height: this.config.height,
                    title: 
                    {
                        text: cubeObj.value.toString() + ' ' + cubeObj.unit,
                        font: {color: this.config.labelColor, size:this.config.textsize},
                    },
                    yaxis: 
                    {
                        tickfont: {color:valueColor, size:this.config.textsize}
                    },
                    xaxis: 
                    {
                        range: [cubeObj.alarm.limits.lolo, cubeObj.alarm.limits.hihi],
                        tickfont: {color:this.config.labelColor, size:this.config.textsize},
                        gridcolor       : this.config.gridColor

                    },
                };
               Plotly.newPlot(this.htmlId, [lowBar, mediumBar, highBar], layout, {'displayModeBar': false});
            }
        }

