//vectorPlotCard
        class VectorPlotCard 
        {
            constructor(app, config)
            {
                this.app = app;
                this.config = config;
            }
            onDocumentReady()
            {
                let containerRowDiv = document.createElement('div');
                containerRowDiv.setAttribute('class', 'row vectorPlotCard-containerRow');
                document.getElementById('appContent').appendChild(containerRowDiv);

                let containerColDiv = document.createElement('div');
                containerColDiv.setAttribute('class', 'col-md-12');
                containerColDiv.setAttribute('align', 'center');
                containerRowDiv.appendChild(containerColDiv);

                let cardDiv = document.createElement('div');
                cardDiv.setAttribute('class', 'card vectorPlotCard');
                containerColDiv.appendChild(cardDiv);

                let titleTable = document.createElement('table');
                titleTable.setAttribute('width', '100%');
                let titleRow  = document.createElement('tr');
                let titleCol  = document.createElement('td');
                titleCol.setAttribute('width', '85%');
                titleCol.setAttribute('align', 'left');
                let titleSpan  = document.createElement('span');
                titleSpan.setAttribute('class', 'vectorPlotCard-title');
                titleSpan.innerHTML = this.config.title;
                titleCol.appendChild(titleSpan);
                titleRow.appendChild(titleCol);
                let buttonCol  = document.createElement('td');
                buttonCol.setAttribute('width', '15%');

                this.displayButton = document.createElement('button');
                this.displayButton.setAttribute('class', 'btn btn-block vectorPlotCard-title-button');
                this.displayButton.innerHTML = '-';
                this.displayButton.onclick = event => {this.toggle()};
                buttonCol.appendChild(this.displayButton);
                titleRow.appendChild(buttonCol);

                this.cardBodyDiv = document.createElement('div');
                this.cardBodyDiv.setAttribute('class', 'vectorPlotCard-body');
                this.cardBodyDiv.setAttribute('align', 'center');
                let cardBodyTable = document.createElement('table');
                cardBodyTable.setAttribute('width', '100%');

                let cardBodyR1 = document.createElement('tr');
                cardBodyTable.appendChild(cardBodyR1);
                let cardBodyR1C1 = document.createElement('td');
                cardBodyR1C1.setAttribute('width', '100%');
                cardBodyR1.appendChild(cardBodyR1C1);
                this.vectorPlotChartDiv = document.createElement('div');
                this.vectorPlotChartDiv.setAttribute('width', '100%');
                this.vectorPlotChartDiv.setAttribute('id', 'vectorPlotChart');
                this.vectorPlotChartDiv.setAttribute('style', 'padding:20px;');
                cardBodyR1C1.appendChild(this.vectorPlotChartDiv);

                this.cardBodyDiv.appendChild(cardBodyTable);

                titleTable.appendChild(titleRow);
                cardDiv.appendChild(titleTable);
                cardDiv.appendChild(this.cardBodyDiv);

                if (this.config.displayOnOpen)
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = '-';
                }
                else
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = '+';
                }
                this.setupPlot();
                window.addEventListener("resize", event => {this.resize()});
            }
            toggle()
            {
                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = '-';
                    if (this.vectorPlotTraces.length > 0)
                    {
                        this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                        Plotly.newPlot('vectorPlotChart', this.vectorPlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                    }
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = '+';
                }

            }
            resize()
            {
                if ((this.cardBodyDiv.style.display != "none") && (this.vectorPlotTraces.length > 0) )
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot('vectorPlotChart', this.vectorPlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                }
            }
            setupPlot()
            {
                this.vectorPlotTraces = [];
                for (let itray = 0; itray < this.config.cubeList.length; ++itray)
                {
                    for (let icube = 0; icube < this.config.cubeList[itray].length; ++icube)
                    {
                        let trace = 
                        {
                            x: [],
                            y: [],
                            name:  this.app.trays[itray].type + '.' + this.app.trays[itray].name + '.' + this.config.cubeList[itray][icube].name,
                            yaxis: this.config.cubeList[itray][icube].yaxis,
                            type: 'scatter',
                            mode: 'lines',
                            line: {color: this.config.cubeList[itray][icube].color}
                        };
                        this.vectorPlotTraces.push(trace);
                    }
                }
            }
            addVectorsToVectorPlot()
            {
                let itray = 0;
                let icube = 0;
                for (let ii = 0; ii < this.vectorPlotTraces.length; ++ii)
                {
                    while (this.config.cubeList[itray].length == 0) 
                    {
                        ++itray;
                        if (itray == this.config.cubeList.length) return;
                    }
                    this.vectorPlotTraces[ii].x = this.app.trays[itray][this.config.cubeList[itray][icube].name].value[0];
                    this.vectorPlotTraces[ii].y = this.app.trays[itray][this.config.cubeList[itray][icube].name].value[1];
                    ++icube;
                    if (icube >= this.config.cubeList[itray].length)
                    {
                        icube = 0;
                        ++itray;
                    }
                }    
                Plotly.newPlot('vectorPlotChart', this.vectorPlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                this.resize();
            }
        }

