        {
            var markup = '<tr>';
            markup = markup + '<td>' + settingsHistory[ii].ipInfo.url + '</td>';
            var topic = settingsHistory[ii].topic.split('/');
            markup = markup + '<td>' 
                +  '<button onclick="displayTray(' + ii.toString() + ')">' + topic[1]  + '-' + topic[2] + '</button>' 
