function selectAxis(itrace,axis)
{
    trayInfo[itrace].axis  = axis;
}
function selectPts(itrace)
{
    trayInfo[itrace].pts = !trayInfo[itrace].pts;
}
function startPlot()
{
    if (!plotStarted)
    {
        $("#startPlotButton").html('Stop Plot');
    }
    else
    {
        $("#startPlotButton").html('Start Plot');
    }
    plotStarted = !plotStarted;
    if (!plotStarted)
    {
        plotTraces = [];
        for (var itrace = 0; itrace < numTraces; ++itrace)
        {
            traySelect[itrace].disabled = false;
            nameSelect[itrace].disabled = false;
            propSelect[itrace].disabled = false;
            attrSelect[itrace].disabled = false;
            $('#plotAxis_' + itrace.toString() + 'y1').prop( "disabled", false );
            $('#plotAxis_' + itrace.toString() + 'y2').prop( "disabled", false );
            $('#pts_' + itrace.toString()).prop( "disabled", false );
        }
        return;
    }
    plotTraces = [];

    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        traySelect[itrace].disabled = true;
        nameSelect[itrace].disabled = true;
        propSelect[itrace].disabled = true;
        attrSelect[itrace].disabled = true;
        $('#plotAxis_' + itrace.toString() + 'y1').prop( "disabled", true );
        $('#plotAxis_' + itrace.toString() + 'y2').prop( "disabled", true );
        $('#pts_' + itrace.toString()).prop( "disabled", true );
        if (trayInfo[itrace].name.length > 0)
        {
            var modeType = 'lines';
            if (trayInfo[itrace].pts) modeType = 'markers';
            var trace =  
            {
                x: [],
                y: [],
                name: trayInfo[itrace].axis + ' ' + trayInfo[itrace].type + '.' + trayInfo[itrace].name + '.' + trayInfo[itrace].prop + '.' + trayInfo[itrace].attr + ' (' + trayInfo[itrace].tray[trayInfo[itrace].attr].unit + ')',
                yaxis: trayInfo[itrace].axis,
                type: 'scatter',
                mode: modeType
            };
            plotTraces.push(trace);
        }
    }
    plotLayout =
    {
        showlegend: true,
        legend: {x: 0,y: 1.35},
        xaxis       : {type: 'date' },
        yaxis: {title: 'y1'},
        yaxis2: {title: 'y2',overlaying: 'y',side: 'right'}
    };

    plotStartTime = new Date().getTime();
    Plotly.newPlot('timePlot', plotTraces, plotLayout);
    plotLength = Number($("#plotLength").val());
}
function upDatePlot()
{
    if (!plotStarted) return;
    var now = new Date().getTime();
    var plotBeginning = now - plotLength * 1000;
    var traceCount = 0;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (trayInfo[itrace].name.length > 0)
        {
            var removeDone = false;
            while(!removeDone)
            {
                if (plotTraces[traceCount].x[0] < plotBeginning)
                {
                    plotTraces[traceCount].x.shift();
                    plotTraces[traceCount].y.shift();
                }
                else
                {
                    removeDone = true;
                }
            }
            plotTraces[traceCount].x.push(now);
            plotTraces[traceCount].y.push(trayInfo[itrace].tray[trayInfo[itrace].attr].value);
            ++traceCount;
        }
    }
    Plotly.newPlot('timePlot', plotTraces, plotLayout);
}

