var projectionFilter =  
{
    projection:
    {
        _id                         :   0, 
        type                        :   1,  
        name                        :   1,  
        timeStamp                   :   1, 
        [msg.payload.tray.attr]     :   1
    } 
};
var queryFilter = 
{
    $and :
    [
        {
            type: msg.payload.tray.type
        },
        {
            name: msg.payload.tray.name
        },
        {
            timeStamp :
            {
                $gte: msg.payload.tray.startDate,
                $lte: msg.payload.tray.stopDate
            }
        }
    ] 

}
var newMsg = 
{
    topic           : 'getArchiveValues',
    userID          : msg.userID,
    payload         : [queryFilter, projectionFilter],
    tray          : msg.payload.tray,
    maxPtsToPlot    : msg.payload.maxPtsToPlot,
    itrace          : msg.payload.tray.itrace
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}

function sortByKey(arr, key) 
{
    return arr.sort( function(a, b) 
        {
            var x = a[key]; 
            var y = b[key];
            return ((x < y) ? -1 : ((x > y) ? 1 : 0));
        } );
}

var numRecords = objectLength(msg.payload);
if (numRecords < 1)
{
    return {
        topic:'getArchiveValues',
        payload:{
            topic           : 'getArchiveValues',
            payload         : {itrace: msg.itrace, trace:null},
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) records[ii] = msg.payload[ii]; 
sortByKey(records, 'timeStamp');
var trace = [];
var maxPts = msg.maxPtsToPlot;
var nstep = numRecords / msg.maxPtsToPlot;
if (nstep < 1.0) 
{
    nstep = 1.0;
    maxPts = numRecords;
}
var icnt = 0;

while(icnt < maxPts)
{
    var index = Math.round(icnt * nstep);
    trace[icnt] = {timeStamp: records[index].timeStamp, value: records[index][msg.tray.attr]};
    ++icnt
}

return {
    topic:'getArchiveValues',
    payload:{
        topic           : 'getArchiveValues',
        payload         : {itrace: msg.itrace, trace:trace},
        userID          : msg.userID,
    }
};

var newMsg = 
{
    topic   : msg.payload.topic, 
    payload : msg.payload.payload, 
    userID  : msg.userID,
    username : msg.token.username
};
return newMsg;

